// Node module: apiconnect-config
'use strict';

/**
 * @constant {Store} PROJECT_STORE Use when storing configuration for a specific project.
 */
module.exports.PROJECT_STORE = '_projectConfig';
/**
 * @constant {Store} USER_STORE Use when storing configuration common to all projects.
 */
module.exports.USER_STORE = '_userConfig';
/**
 * @constant {Store} TOKEN_STORE Use when storing JWT tokens common to all projects.
 */
module.exports.TOKEN_STORE = '_tokenConfig';

/**
 * @constant Default concurrency used to throttle control the number of requests made against the backend
 */
module.exports.DEFAULT_CONCURRENCY = 5;
