// Node module: apiconnect-config

'use strict';

module.exports = getUserConfigDir;

var mkdirp = require('mkdirp');
var osenv = require('osenv');
var path = require('path');

function getUserConfigDir() {
  process.env.APIC_CLI_CONFIG_DIR = process.env.APIC_CLI_CONFIG_DIR || '.apiconnect';
  var configDir = process.env.APIC_CONFIG_PATH || path.resolve(osenv.home(), process.env.APIC_CLI_CONFIG_DIR);
  mkdirp.sync(configDir);
  return configDir;
};

